mapper.insert:

	SELECT NVL(MAX(department_id),0) +10
	FROM   departments

INSERT INTO departments
		(
			department_id
			, department_name

				, manager_id


				, location_id

		)
VALUES  (
			#{departmentId}
			, #{departmentName}

				, #{managerId}


				, #{locationId}

		)

mapper.update:
UPDATE departments
SET
	department_name = #{departmentName}

		, manager_id = #{managerId}


		, location_id = #{locationId}

WHERE department_id = #{departmentId}
